-- Insert activities for TYPICO tracks (Days 1-5)

-- TYPICO_1_3 activities
INSERT INTO public.activities (track_id, day_index, title, instructions, media_url) VALUES
('4ffe9a96-b803-42a3-a1b4-30ac3da9353a', 1, 'Brincando de "Cadê… Achou!"', 'Esconda o rosto atrás das mãos e diga "cadê o(a) {{child_name}}?". Abra e diga "achou!" incentive a criança a imitar "achou".', 'typico_1_3_d1.mp4'),
('4ffe9a96-b803-42a3-a1b4-30ac3da9353a', 2, 'Sons de Animais: Muu & Au‑au', 'Mostre cartão de vaca e diga "muu". Peça para {{child_name}} repetir. Depois, cachorro "au‑au".', 'typico_1_3_d2.mp4'),
('4ffe9a96-b803-42a3-a1b4-30ac3da9353a', 3, 'Bolhas de Sabão Falantes', 'Estoure bolhas dizendo "pá". Cada bolha estourada incentive a criança a dizer "pá".', 'typico_1_3_d3.mp4'),
('4ffe9a96-b803-42a3-a1b4-30ac3da9353a', 4, 'Livro de Figuras: "Cadê o gato?"', 'Abra livro, pergunte "cadê o gato?". Quando apontar, diga "miau" e peça para imitar.', 'typico_1_3_d4.mp4'),
('4ffe9a96-b803-42a3-a1b4-30ac3da9353a', 5, 'Bater Palmas no Ritmo', 'Bata palmas devagar dizendo "pa‑pa". Depois mais rápido. Peça para {{child_name}} copiar.', 'typico_1_3_d5.mp4'),

-- TYPICO_4_6 activities
('0123c14b-13c9-4dea-8d58-5ed013f97c13', 1, 'Caça ao Tesouro de Palavras', 'Espalhe 5 cartões com imagens. Peça que {{child_name}} encontre cada item e diga o nome.', 'typico_4_6_d1.mp4'),
('0123c14b-13c9-4dea-8d58-5ed013f97c13', 2, 'Rimas Divertidas', 'Diga "casa–asa". Peça para achar rima de "sol". Ajude até dizer "rol".', 'typico_4_6_d2.mp4'),
('0123c14b-13c9-4dea-8d58-5ed013f97c13', 3, 'Telefone Sem Fio', 'Sussurre uma frase curta ("o gato dorme"). Criança repete em voz alta; compare.', 'typico_4_6_d3.mp4'),
('0123c14b-13c9-4dea-8d58-5ed013f97c13', 4, 'Descreva o Sabor', 'Ofereça fruta e pergunte "como é o gosto?". Incentive uso de adjetivos ("doce, azedo").', 'typico_4_6_d4.mp4'),
('0123c14b-13c9-4dea-8d58-5ed013f97c13', 5, 'Histórias em 3 Cartões', 'Organize sequências (acordar‑café‑escola). Peça para contar a história na ordem certa.', 'typico_4_6_d5.mp4'),

-- TYPICO_7_10 activities
('84a38047-a66c-4b50-89c4-d2b17a6e6033', 1, 'Repórter por um Dia', 'Entrevistar um familiar com 3 perguntas "o que/onde/por quê" e apresentar resposta.', 'typico_7_10_d1.mp4'),
('84a38047-a66c-4b50-89c4-d2b17a6e6033', 2, 'Debate do "E se…?"', 'Proponha: "E se tivéssemos férias todos os dias?". Criança defende prós em 3 frases.', 'typico_7_10_d2.mp4'),
('84a38047-a66c-4b50-89c4-d2b17a6e6033', 3, 'Palavra‑chave do Dia', 'Escolha palavra difícil ("ecosistema"). Peça definição própria e use em frase.', 'typico_7_10_d3.mp4'),
('84a38047-a66c-4b50-89c4-d2b17a6e6033', 4, 'Histórias Alternativas', 'Leia final de livro infantil. Peça que crie final diferente com 3 sentenças.', 'typico_7_10_d4.mp4'),
('84a38047-a66c-4b50-89c4-d2b17a6e6033', 5, 'Crie sua Piada', 'Explique estrutura "pergunta / resposta engraçada". Ajude a escrever piada curta.', 'typico_7_10_d5.mp4'),

-- TYPICO_11_14 activities
('d2aa14e3-f69f-4f64-af4b-130442c41f89', 1, 'Mini Podcast', 'Grave áudio 1 min opinando sobre filme. Foque em argumento e conclusão.', 'typico_11_14_d1.mp4'),
('d2aa14e3-f69f-4f64-af4b-130442c41f89', 2, 'Palavra Persuasiva', 'Escolha produto imaginário e crie slogan de 15 palavras.', 'typico_11_14_d2.mp4'),
('d2aa14e3-f69f-4f64-af4b-130442c41f89', 3, 'Improviso de 30 seg', 'Sorteie tema aleatório e fale 30 seg sem "ééé". Grave e ouça juntos.', 'typico_11_14_d3.mp4'),
('d2aa14e3-f69f-4f64-af4b-130442c41f89', 4, 'Debate Relâmpago', '2 min para preparar contra‑argumentos sobre tema cotidiano (uniforme escolar). Apresentar.', 'typico_11_14_d4.mp4'),
('d2aa14e3-f69f-4f64-af4b-130442c41f89', 5, 'Análise de Meme', 'Mostrar meme popular; peça explicação de humor e contexto em 3 frases.', 'typico_11_14_d5.mp4');