-- TEA_1_3 activities
INSERT INTO public.activities (track_id, day_index, title, instructions, media_url) VALUES
('b3af240a-3a5e-4dd5-af91-51e3dfab56b1', 1, 'Olhe Onde Eu Olho', 'Segure brinquedo, espere olhar; diga "olhou!". Reforce atenção conjunta.', 'tea_1_3_d1.mp4'),
('b3af240a-3a5e-4dd5-af91-51e3dfab56b1', 2, 'Troca de Turnos com Carrinho', 'Empurre carrinho e espere criança empurrar de volta. Nomeie "minha vez / sua vez".', 'tea_1_3_d2.mp4'),
('b3af240a-3a5e-4dd5-af91-51e3dfab56b1', 3, 'Escolha entre Dois', 'Mostre leite vs suco; criança aponta. Peça verbalizar "suco".', 'tea_1_3_d3.mp4'),
('b3af240a-3a5e-4dd5-af91-51e3dfab56b1', 4, 'Música Pausada', 'Cante "atirei o pau"; pause antes da rima e espere completar.', 'tea_1_3_d4.mp4'),
('b3af240a-3a5e-4dd5-af91-51e3dfab56b1', 5, 'Imitar Ações Simples', 'Bater tambor; peça imitar 3 vezes.', 'tea_1_3_d5.mp4'),

-- TEA_4_6 activities
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 1, 'Sequência de Imagem PECS', 'Monte frase com figuras "quero + bala". Criança troca cartão.', 'tea_4_6_d1.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 2, 'Turn‑taking de Perguntas', 'Pergunte "o que é isso?". Criança responde e faz pergunta igual.', 'tea_4_6_d2.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 3, 'Histórias Sociais Curtas', 'Leia história de amizade. Pergunte "o que ele sentiu?".', 'tea_4_6_d3.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 4, 'Eco Funcional', 'Diga "eu quero água"; peça repetir, depois usar para pedir real.', 'tea_4_6_d4.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 5, 'Caça Objetos por Cor', 'Peça "traga algo vermelho" reforçando identificação.', 'tea_4_6_d5.mp4'),

-- TEA_7_10 activities
('352fbd89-5ef6-4982-944a-f317ed6dbd16', 1, 'Script de Cumprimento', 'Treine "Oi, tudo bem?" com gesto de olhar.', 'tea_7_10_d1.mp4'),
('352fbd89-5ef6-4982-944a-f317ed6dbd16', 2, 'Conversa de 3 Turnos', 'Tema favorito; objetivo: manter 3 trocas sem mudar assunto.', 'tea_7_10_d2.mp4'),
('352fbd89-5ef6-4982-944a-f317ed6dbd16', 3, 'Cartões de Emoções', 'Mostre expressão; criança nomeia e conta situação parecida.', 'tea_7_10_d3.mp4'),
('352fbd89-5ef6-4982-944a-f317ed6dbd16', 4, 'Pergunta "Por quê?"', 'Leia fato simples; pergunte "por quê?" para incentivar explicação causal.', 'tea_7_10_d4.mp4'),
('352fbd89-5ef6-4982-944a-f317ed6dbd16', 5, 'Histórias em Quadrinhos de Rotina', 'Crie 4 quadros sobre "arrumar mochila". Criança descreve cada quadro.', 'tea_7_10_d5.mp4'),

-- TEA_11_14 activities
('10caf523-818f-4736-9ed8-517bd5430ba5', 1, 'Gírias e Significados', 'Liste 3 gírias atuais e peça explicar contexto.', 'tea_11_14_d1.mp4'),
('10caf523-818f-4736-9ed8-517bd5430ba5', 2, 'Ironia Detectives', 'Leia frase irônica; pergunte se é sério ou brincadeira.', 'tea_11_14_d2.mp4'),
('10caf523-818f-4736-9ed8-517bd5430ba5', 3, 'Debate em Dupla', 'Defender escolha de filme em 1 min; ouvir outro e contra‑argumentar.', 'tea_11_14_d3.mp4'),
('10caf523-818f-4736-9ed8-517bd5430ba5', 4, 'Planejar Saída', 'Criar roteiro para passeio; incluir horário, transporte e custo.', 'tea_11_14_d4.mp4'),
('10caf523-818f-4736-9ed8-517bd5430ba5', 5, 'Troca de Piadas', 'Cada um conta piada; discutir por que é engraçada.', 'tea_11_14_d5.mp4');