-- Adicionar atividades adicionais para TEA 4-6 anos (track que Benicio agora usa)
INSERT INTO activities (track_id, title, instructions, day_index, media_url) VALUES
-- Dia 1
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Rotina Visual', 'Use imagens sequenciais para mostrar a rotina do dia e estimular a comunicação.', 1, 'tea_4_6_d1_2.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Brincadeira de Turnos', 'Jogue bola alterando turnos, sempre verbalizando "minha vez", "sua vez".', 1, 'tea_4_6_d1_3.mp4'),
-- Dia 2  
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Contato Visual com Música', 'Cante músicas favoritas mantendo contato visual e pausando para esperar resposta.', 2, 'tea_4_6_d2_2.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Imitação de Ações', 'Faça ações simples como bater palmas, acenar, e espere a criança imitar.', 2, 'tea_4_6_d2_3.mp4'),
-- Dia 3
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Escolhas Estruturadas', 'Ofereça duas opções visuais (brinquedos, comidas) e espere a comunicação.', 3, 'tea_4_6_d3_2.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Causa e Efeito', 'Use brinquedos que reagem (botões, luzes) para estimular comunicação intencional.', 3, 'tea_4_6_d3_3.mp4'),
-- Dia 4
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Brincadeira Sensorial', 'Explore texturas diferentes nomeando e descrevendo as sensações.', 4, 'tea_4_6_d4_2.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Histórias Interativas', 'Conte histórias simples com pausas para a criança completar ou responder.', 4, 'tea_4_6_d4_3.mp4'),
-- Dia 5
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Jogo de Cores', 'Nomeie cores de objetos e peça para a criança encontrar cores iguais.', 5, 'tea_4_6_d5_2.mp4'),
('fd1b5eae-f5c7-4d45-b931-6decccc9ab49', 'Comunicação com Gestos', 'Ensine gestos simples como "mais", "acabou", "ajuda" em contextos naturais.', 5, 'tea_4_6_d5_3.mp4');

-- Adicionar atividades para ATRASO 4-6 anos (track do Enzo)
INSERT INTO activities (track_id, title, instructions, day_index, media_url) VALUES
-- Dia 1
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Nomeação de Objetos', 'Mostre objetos familiares e estimule a criança a nomear cada um.', 1, 'atraso_4_6_d1_2.mp4'),
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Sons do Cotidiano', 'Identifique sons da casa (telefone, campainha) e imite cada um.', 1, 'atraso_4_6_d1_3.mp4'),
-- Dia 2
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Frases de 2 Palavras', 'Estimule frases simples como "quero água", "mais biscoito", "tchau mamãe".', 2, 'atraso_4_6_d2_2.mp4'),
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Exercícios de Sopro', 'Use apitos, flautas simples ou sopre bolinhas de sabão.', 2, 'atraso_4_6_d2_3.mp4'),
-- Dia 3
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Rimas Simples', 'Cante músicas com rimas e pause para a criança completar.', 3, 'atraso_4_6_d3_2.mp4'),
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Perguntas e Respostas', 'Faça perguntas simples sobre preferências: "Você quer maçã ou banana?".', 3, 'atraso_4_6_d3_3.mp4'),
-- Dia 4
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Descrição de Ações', 'Narrem juntos ações cotidianas: "estou comendo", "papai dirigindo".', 4, 'atraso_4_6_d4_2.mp4'),
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Jogo da Memória Verbal', 'Repita sequências de 2-3 palavras e peça para a criança repetir.', 4, 'atraso_4_6_d4_3.mp4'),
-- Dia 5
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Contação Participativa', 'Conte histórias onde a criança precisa fazer sons ou falar personagens.', 5, 'atraso_4_6_d5_2.mp4'),
('d4f91b8a-be7c-4b01-b59a-f54add188093', 'Expressão de Sentimentos', 'Use caretas e gestos para expressar alegre, triste, bravo e verbalize.', 5, 'atraso_4_6_d5_3.mp4');